function sleep(ms){return new Promise((resolve=>setTimeout(resolve,ms)))}function getRandomInt(min,max){min=Math.ceil(min);max=Math.floor(max);return Math.floor(Math.random()*(max-min)+min)}async function shiftHorizontalRandom(){var elems=Array.from(document.body.getElementsByTagName("*"));while(true){for(elem in elems){elems[elem].style.transform="none";elems[elem].style.transform=`translateX(${getRandomInt(-200,100)}px)`}await sleep(250)}}async function shiftVerticalRandom(){var elems=Array.from(document.body.getElementsByTagName("*"));while(true){for(elem in elems){elems[elem].style.transform="none";elems[elem].style.transform=`translateY(${getRandomInt(-200,100)}px)`}await sleep(250)}}async function shiftRandom(){shiftHorizontalRandom();shiftVerticalRandom()}async function rotateRandom(){var elems=Array.from(document.body.getElementsByTagName("*"));while(true){for(elem in elems){elems[elem].style.transform=`rotate(${getRandomInt(0,359)}deg)`}await sleep(250)}}async function runAway(){var elems=Array.from(document.body.getElementsByTagName("*"));for(elem in elems){elems[elem].addEventListener("mouseover",moveFromMouse)}while(true){await sleep(250)}}async function moveFromMouse(e){e.target.style.transform="none";e.target.style.transform=`translate(${getRandomInt(-100,50)}px, ${getRandomInt(-100,50)}px)`}async function colorsRandom(){var elems=Array.from(document.body.getElementsByTagName("*"));while(true){for(elem in elems){let currentBack=elems[elem].style.backgroundColor;let currentColor=elems[elem].style.color;elems[elem].animate([{backgroundColor:currentBack,color:currentColor},{backgroundColor:`rgb(${getRandomInt(0,256)},${getRandomInt(0,256)},${getRandomInt(0,256)})`,color:`rgb(${getRandomInt(0,256)},${getRandomInt(0,256)},${getRandomInt(0,256)})`},{backgroundColor:currentBack,color:currentColor}],{duration:2500,easing:"ease-in-out",iterations:1})}await sleep(2500)}}async function opacityRandom(){var elems=Array.from(document.body.getElementsByTagName("*"));while(true){for(elem in elems){let opacity=Math.random()*2;if(elems[elem].classList.contains("dontNuke")){opacity=1}elems[elem].animate([{opacity:1},{opacity:opacity},{opacity:1}],{duration:5e3,easing:"ease-in-out",iterations:1})}await sleep(5e3)}}async function makeItHell(){shiftRandom();runAway();colorsRandom();opacityRandom()}
